	[1031803] = {
		unlock_by_time = "",
		name = "丽天宫",
		unlock_by_condition_id = 1300013,
		archive_id = 1031803,
		archive_icon = "TextureConfig/OperationDiary/OperationDiary_31802",
		desc = "天之子的居所，坐落于丽天市，是整个笹波的政治心脏。也被民间称为「皇城」。\n\n丽天宫于惑山之乱后，在原址基础上大规模翻新重建，以独特的设计思路筑于高楼之上，俯瞰着整个上京区乃至笹波。作为丽天市的地标建筑，经过历代的修缮和改建，已经成为代表着笹波建筑艺术的最高之作。",
		tag_id = 3,
		archive_parameter = 0,
		archive_type = 1,
		chapter_id = {
			10118,
			11118
		}
	},
	[1031829] = {
		unlock_by_time = "",
		name = "Ω装具",
		unlock_by_condition_id = 1300014,
		archive_id = 1031829,
		archive_icon = "TextureConfig/OperationDiary/OperationDiary_31803",
		desc = "科尔盖针对修正者神能的主要研究成果之一，仅对特定修正者生效的增幅装具。\n\nΩ装具装载了一套完整的神能回路抑制、增幅系统，在通过链接权钥激活修正者自身最高权限后，可通过注入「识别干扰注射剂」，临时性地篡改修正者的权限，使其短暂获得原初级别的战斗能力。其使用后果暂不明确，可预见的是将会对修正者的核心数据造成严重的损害。\n\n出于某些不明原因，这项研究已经搁置，Ω装具目前仅此一套。",
		tag_id = 3,
		archive_parameter = 0,
		archive_type = 1,
		chapter_id = {
			10118,
			11118
		}
	},
	[1041105] = {
		unlock_by_time = "",
